mapper.delete:
update tbl_transport_card set 
is_invalid=1 
where id=#{id}

update tbl_vehicle set 
transport_card_id = 0
where transport_card_id = #{transportCardId}

mapper.select:
select from tbl_transport_card t left join tbl_vehicle c on t.transport_card_id=c.transport_card_id where t.is_invalid=0
and t.transport_card_id = #{entity.transportCardId}
order by t.id asc limit #{page.startRow}, #{page.rows}
