mapper.delete:
update tbl_trans_company set 
is_invalid=1 
where id=#{id}

mapper.select:
select count(*) from tbl_trans_company where is_invalid=0 

	and name like CONCAT('%',#{name},'%')


	and parent_company_id = #{superior.id}

select from tbl_trans_company t1 left join tbl_trans_company t2 on t1.parent_company_id=t2.id where t1.is_invalid=0
and t1.name like CONCAT('%',#{entity.name},'%')
and t1.parent_company_id = #{entity.superior.id}
order by t1.id asc limit #{page.startRow}, #{page.rows}
