mapper.update:
update tbl_user set 
is_invalid = 1
where id=#{id}

mapper.select:
select

from tbl_user u left join tbl_role r2 on u.app_role_id = r2.id, tbl_role r
where u.is_invalid = 0 and u.role_id = r.id and u.id=#{id}


select

from tbl_user u left join tbl_role r2 on u.app_role_id = r2.id, tbl_role r
where u.is_invalid = 0 and u.role_id = r.id and binary u.account = #{account}

select

from tbl_user u left join tbl_role r2 on u.app_role_id = r2.id, tbl_role r
where u.is_invalid = 0 and u.role_id = r.id and u.account!='admin' and u.identity_card = #{identityCard}

mapper.update:
update tbl_user set
password=#{password}
where id=#{id}

update tbl_user set
role_id=2
where role_id=#{roleId}

update tbl_user set
app_role_id=0
where app_role_id=#{appRoleId}

mapper.select:
select count(*) from tbl_user where is_invalid = 0 and account!='admin' and role_id!=#{role.id}

    and account like CONCAT('%',#{account},'%')


    and name like CONCAT('%',#{name},'%')

select

from tbl_user u left join tbl_role r2 on u.app_role_id = r2.id, tbl_role r
where u.is_invalid = 0 and u.role_id = r.id and u.account!='admin' and u.role_id!=#{entity.role.id}

order by u.id asc
limit #{page.startRow}, #{page.rows}
