mapper.update:
update tbl_user set 
is_invalid = 1
where id=#{id}

update tbl_user set
password=#{password}
where id=#{id}

update tbl_user set
role_id=2
where role_id=#{roleId}

update tbl_user set
app_role_id=0
where app_role_id=#{appRoleId}

mapper.select:
select count(*) from tbl_user where is_invalid = 0 and account!='admin' and role_id!=#{role.id}

    and account like CONCAT('%',#{account},'%')


    and name like CONCAT('%',#{name},'%')

SELECT
FROM `tbl_user` u LEFT JOIN `tbl_trans_company` c ON u.`trans_company_id` = c.`id` LEFT JOIN `tbl_role` r2 ON u.`app_role_id` = r2.`id`, `tbl_role` r WHERE u.`is_invalid` = 0 AND u.`role_id` = r.`id` AND u.`account` != 'admin' AND u.`role_id` != #{entity.role.id}
ORDER BY u.`id` ASC LIMIT #{page.startRow}, #{page.rows}
