mapper.select:
select count(*) from tbl_vehicle_seal_status s,tbl_vehicle v
where s.vehicle_id = v.id and s.status_incident in (1,2,3,4)

	and s.trigger_time between #{begin} and #{end}


	and s.trigger_type = #{type}


	and v.vehicle_number like CONCAT('%',#{carNumber},'%')

SELECT
FROM tbl_vehicle_seal_status s, tbl_vehicle v WHERE s.vehicle_id = v.id AND s.status_incident IN ( 1, 2, 3, 4 )
AND s.trigger_time BETWEEN #{entity.begin} AND #{entity.end}
AND s.trigger_type = #{entity.type}
AND v.vehicle_number LIKE CONCAT('%',#{entity.carNumber},'%')
ORDER BY s.id ASC limit #{page.startRow}, #{page.rows}
