mapper.select:
SELECT count(*) FROM tbl_lock_status l, tbl_vehicle v WHERE l.vehicle_id = v.id
AND l.change_time BETWEEN #{begin} AND #{end}
AND l.status = #{status}
AND v.vehicle_number LIKE CONCAT('%',#{carNumber},'%')

SELECT
FROM tbl_lock_status l, tbl_vehicle v, tbl_lock tl WHERE l.vehicle_id = v.id AND l.vehicle_id = tl.vehicle_id AND l.store_id = tl.store_id AND l.seat = tl.seat AND l.seat_index = tl.seat_index
AND l.change_time BETWEEN #{entity.begin} AND #{entity.end}
AND l.status = #{entity.status}
AND v.vehicle_number LIKE CONCAT('%',#{entity.carNumber},'%')
ORDER BY l.id DESC LIMIT #{page.startRow}, #{page.rows}
