mapper.select:
select count(*) from tbl_remote_control r left join tbl_vehicle_seal_status c on r.vehicle_seal_status_id=c.id, tbl_vehicle v where c.vehicle_id = v.id
and r.happen_time between #{begin} and #{end}
and r.type = #{type}
and v.vehicle_number like CONCAT('%',#{carNumber},'%')

select

from (tbl_remote_control r left join 
		(tbl_vehicle_seal_status c left join tbl_vehicle_track t on c.vehicle_track_id=t.id)
	on r.vehicle_seal_status_id=c.id),tbl_user u,tbl_vehicle v
where c.vehicle_id = v.id and r.user_id = u.id

	and r.happen_time between #{entity.begin} and #{entity.end} 


	and r.type = #{entity.type}


	and v.vehicle_number like CONCAT('%',#{entity.carNumber},'%')

order by r.id asc  
limit #{page.startRow}, #{page.rows}
