mapper.update:
update tbl_vehicle set
vehicle_device_id=#{terminalId}
where vehicle_number=#{carNumber}

update tbl_vehicle set
vehicle_device_id = 0
where vehicle_number=#{carNumber} and vehicle_device_id=#{terminalId}

update tbl_vehicle set 
is_invalid=1 
where id=#{id}

mapper.select:
select count(*) from tbl_vehicle where is_invalid=0 

	and vehicle_number = #{carNumber}


	and trans_company_id = #{transCompany.id}

SELECT
FROM tbl_vehicle c LEFT JOIN tbl_device d ON c.vehicle_device_id = d.device_id LEFT JOIN tbl_trans_company t ON c.trans_company_id = t.id AND t.is_invalid = 0 WHERE c.is_invalid = 0
and c.vehicle_number = #{entity.carNumber}
and c.trans_company_id = #{entity.transCompany.id}
order by c.id asc limit #{page.startRow}, #{page.rows}

SELECT v.trans_company_id id, c.`name` FROM tbl_vehicle v LEFT JOIN tbl_trans_company c ON v.trans_company_id = c.id WHERE v.is_invalid = 0
AND v.trans_company_id = #{comId}
GROUP BY v.trans_company_id;

SELECT v.trans_company_id id, c.`name` FROM tbl_vehicle v LEFT JOIN tbl_trans_company c ON v.trans_company_id = c.id WHERE v.is_invalid = 0 AND v.vehicle_device_id != 0
AND v.trans_company_id = #{comId}
GROUP BY v.trans_company_id;

SELECT v.trans_company_id id, c.`name` FROM tbl_vehicle_realtime_status s, tbl_vehicle v LEFT JOIN tbl_trans_company c ON v.trans_company_id = c.id WHERE v.is_invalid = 0 AND v.vehicle_device_id != 0 AND v.id = s.vehicle_id AND s.is_online = 1
AND v.trans_company_id = #{comId}
GROUP BY v.trans_company_id;


